import { useD3 } from "../hooks/useD3";
import React from "react";
import * as d3 from "d3";

const margin = {
  top: 50,
  right: 50,
  bottom: 50,
  left: 50,
};

function PieChart({ data }) {
  const ref = useD3((svg) => {
    console.log(data);
    var pie = d3.pie();
    console.log(pie(data));
  });

  return (
    <>
      <svg ref={ref}></svg>
    </>
  );
}

export default PieChart;
