import { useD3 } from "../hooks/useD3";
import React from "react";
import * as d3 from "d3";

const width = 200;
const height = 120;

function Barsvg({ data }) {
  const ref = useD3(
    (svg) => {
      const margin = { top: 10, right: 10, bottom: 30, left: 40 };

      svg
        .attr("preserveAspectRatio", "xMinYMin meet")
        .attr("viewBox", "0 0 200 120")
        .classed("svg-content", true);

      const x = d3
        .scaleBand()
        .domain(data.map((d) => d.year))
        .rangeRound([margin.left, width - margin.right])
        .padding(0.1);

      const y1 = d3
        .scaleLinear()
        .domain([0, d3.max(data, (d) => d.sales)])
        .rangeRound([height - margin.bottom, margin.top]);

      const xAxis = (g) =>
        g
          .attr("transform", `translate(0,${height - margin.bottom})`)
          .call(
            d3
              .axisBottom(x)
              .tickValues(
                d3
                  .ticks(...d3.extent(x.domain()), width / 40)
                  .filter((v) => x(v) !== undefined)
              )
              .tickSizeOuter(0)
          )
          .style(`font-family`, "Karla")
          .style("color", "#f8f9fa");

      const y1Axis = (g) =>
        g
          .attr("transform", `translate(${margin.left},0)`)
          .style("font-size", 10)
          .style("color", "#f8f9fa")
          .style("font-family", "Karla")
          .call(d3.axisLeft(y1).ticks(null, "s"))
          .call((g) => g.select(".domain").remove())
          .call((g) =>
            g
              .append("text")
              .attr("x", -margin.left)
              .attr("y", 10)
              .attr("fill", "currentColor")
              .attr("text-anchor", "start")
              .text(data.y1)
          );

      svg.select(".x-axis").call(xAxis);
      svg.select(".y-axis").call(y1Axis);

      // Grid lines
      svg
        .append("g")
        .attr("class", "grid")
        .style("color", "#9FAAAE")
        .attr("transform", `translate(${margin.left},0)`)
        .call(d3.axisLeft().scale(y1).tickSize(-width, 0, 0).tickFormat(""));
      const barGroups = svg
        .append("g")
        .attr("class", "plot-area")
        .attr("fill", "steelblue")
        .selectAll(".bar")
        .data(data)
        .join("rect")
        .attr("class", "bar")
        .attr("fill", (d) => {
          switch (true) {
            case d.sales >= 5000: {
              console.log(d.sales);
              return `#ff1d58`;
            }
            case d.sales >= 4000: {
              return `#fff685`;
            }
            case d.sales >= 3000: {
              return `#5cbdb9`;
            }
            default: {
              return `steelblue`;
            }
          }
        })
        .style("stroke", `black`)
        .attr("x", (d) => x(d.year))
        .attr("width", x.bandwidth())
        .attr("y", (d) => y1(d.sales))
        .attr("height", (d) => y1(0) - y1(d.sales))
        .append("title")
        .text((d) => `Sales were ${d.sales} in ${d.year}`)
        .on('mouseenter', function (actual, i) {
          d3.selectAll('.value')
            .attr('opacity', 0)

          d3.select(this)
            .transition()
            .duration(300)
            .attr('opacity', 0.6)
            .attr('x', (a) => x(a.language) - 5)
            .attr('width', x.bandwidth() + 10)

          const y = y1(actual.value)

          const line = svg.append('line')
            .attr('id', 'limit')
            .attr('x1', 0)
            .attr('y1', y)
            .attr('x2', width)
            .attr('y2', y)

          barGroups.append('text')
            .attr('class', 'divergence')
            .attr('x', (a) => x(a.language) + x.bandwidth() / 2)
            .attr('y', (a) => y1(a.value) + 30)
            .attr('fill', 'white')
            .attr('text-anchor', 'middle')
            .text((a, idx) => {
              const divergence = (a.value - actual.value).toFixed(1)

              let text = ''
              if (divergence > 0) text += '+'
              text += `${divergence}%`

              return idx !== i ? text : '';
            })

        })
        .on('mouseleave', function () {
          d3.selectAll('.value')
            .attr('opacity', 1)

          d3.select(this)
            .transition()
            .duration(300)
            .attr('opacity', 1)
            .attr('x', (a) => x(a.language))
            .attr('width', x.bandwidth())

          svg.selectAll('#limit').remove()
          svg.selectAll('.divergence').remove()
        })
    },
    [data.length]
  );

  return (
    <>
      <svg
        ref={ref}
        viewBox={`0 0 ${height} ${width}`}
        preserveAspectRatio="xMinYMin meet"
      >
        {/* <g className="plot-area" /> */}
        <g className="x-axis" />
        <g className="y-axis" />
      </svg>
    </>
  );
}

export default Barsvg;
